import React, { Component } from 'react'
import styled from 'styled-components'
import MainImage from '../../assets/images/yellowDoor.jpg'
const HomeImage = styled.div`
    width: 100%;
    height: 600px;
    background: linear-gradient( rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.8) ), url('${MainImage}');
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
`
const Title = styled.h1`
  width: 50%;
  font-weight: 400;
  position: absolute;
  left: 25%;
  text-align: center;
  font-size: 60px;
  top: 15%;
  color: white;
`
const SearchComponent = styled.div`
  width: 70%;
  height: 50px;
  top: 45%;
  position: absolute;
  left: 15%;
  background-color: yellow;
`
const SearchTextBox = styled.input`
  width: 89%;
  height: 89%;
  float: left;
  font-size: 16px;
`
const SearchButton = styled.button`
  width: 10%;
  height: 100%;
  float: left;
  margin: auto;
  color: white;

`
class Home extends Component {
  render() {
    return (
     <HomeImage>
        <Title>Find the Perfect Home</Title>
        <SearchComponent>
          <SearchTextBox type='text' placeholder='Enter an address, neighborhood, city, or ZIP code' />
          <SearchButton/>
        </SearchComponent>
      </HomeImage>
    )
  }
}
export default Home
